sp_widget.client_script:
c.updateNote = function(updateType) {
    c.server.get({
      action: 'updateNote',
      noteID: c.data.noteID,
      noteBody: c.data.note,
      noteTitle: c.data.title
    }).then(function(r) {
    });
  }

sp_widget.script:
(function() {
    // Check if input and input.noteID exist
    if (input && input.noteID) {
        var note = new GlideRecord('x_snc_createnotes_note');

        // Try to retrieve the note with the provided noteID
        if (note.get(input.noteID)) {
            // Handle 'getNote' action: Return note details
            if (input.action === 'getNote') {
                data.note = {};
                // Get specific fields from the note record
                $sp.getRecordValues(data.note, note, "title, note, sys_id");
            }
            // Handle 'updateNote' action: Update note fields
            else if (input.action === 'updateNote') {
                // Validate that input.noteTitle and input.noteBody exist
                if (input.noteTitle && input.noteBody) {
                    note.title = input.noteTitle;
                    note.note = input.noteBody;
                    note.update();
                } else {
                    // Handle the case where noteTitle or noteBody are missing
                    data.error = "Note title or body is missing.";
                }
            } else {
                // Handle unknown actions
                data.error = "Invalid action specified.";
            }
        } else {
            // Handle the case where the note with the provided noteID does not exist
            data.error = "Note with the specified ID not found.";
        }
    } else {
        // Handle the case where input or input.noteID is missing
        data.error = "Missing or invalid input.";
    }
})();
